var winston = require("winston");

var logger = new winston.Logger({
    transports: [
    /*
        new winston.transports.Console({
        	colorize: true, 
        	level: "info", 
        	//prettyPrint: true,
        	timestamp: true,
    	}),
        */
        new winston.transports.File({
        	timestamp: true,
        	maxsize: 2000000,
        	maxFiles: 5,
        	tailable: true,
        	filename: "/var/log/nascent.log"
		})
    ]
});

